function bindAnchorsToModal(n){$(function(){$.ajaxSetup({cache:!1});$("a[href*='/"+n+"?']").click(function(n){n.preventDefault();showModal(this.href)})})}function showModal(n,t){var i=$("<div>").prependTo("body");i.load(n,null,function(){i.find(".modal").modal("show")});i.on("hidden.bs.modal",function(){t!=undefined&&t(i);i.off();i.remove()})}function showConfirm(n,t){showModal(n,function(n){t(n.find(".modal").data("result")===!0)})}function confirmAndPost(n){showConfirm(n,function(t){t&&$("<form>").appendTo("body").attr({action:n,method:"post"}).submit()})}$(function(){$(document).ajaxSend(function(){$("#throbber").show()});$(document).ajaxComplete(function(){$("#throbber").hide()})});